import { createStore } from 'redux'

const initState = {
    goodslist: { id: null, productName: '', price: {} }
}

const reducer = function (state = initState, action) {
    switch (action.type) {
        case 'add_to_goods':
            console.log(action.goods);
            return {
                goodslist: {
                    ...state.goodslist,
                    ...action.goods
                }
            }
        default:
            return state
    }

    // return state
}


const store = createStore(reducer)


export default store
