import axios from 'axios'
const com = axios.create({
    baseURL: 'http://47.115.142.170:60005/'
});

//登录数据
export const login = async (params, config = {}) => {
    console.log(params, config)
    let { data } = await com.get('user/login', {
        ...config,
        params
    })
    return data
}

export default {
    login
}
